 (17,'Nombre de conjoints de femmes enceintes s&eacute;ropositives au VIH d&eacute;pist&eacute;s positifs',NULL,'SELECT COUNT(DISTINCT pregnant_patient_id) nb\r\nFROM (\r\n       SELECT\r\n         p.pregnant_patient_id\r\n       FROM ptme_mother_followup pmf, ptme_pregnant_patient p\r\n       WHERE\r\n         pmf.pregnant_patient_id = p.pregnant_patient_id AND\r\n         pmf.spousal_screening_date BETWEEN :startDate AND :endDate AND\r\n         pmf.spousal_screening_result = 1 AND\r\n         pmf.location_id = :locationId\r\n       UNION\r\n\r\n       SELECT\r\n         c.pregnant_patient_id\r\n       FROM ptme_consultation c, ptme_prenatal p, ptme_hiv_service h\r\n       WHERE\r\n         c.consultation_id = h.hiv_service_id AND\r\n         (h.hiv_status_at_reception = 1 OR h.test_result = 1) AND\r\n         c.consultation_id = p.consultation_id AND\r\n         c.consultation_date BETWEEN :startDate AND :endDate AND\r\n         p.spousal_screening_result = 1 AND\r\n         c.location_id = :locationId\r\n     ) _','I17','17dbb54d-17b8-4181-9858-53ae02dd2335',1,'2018-11-12 08:51:16',1,'2019-01-15 09:15:44',0,NULL,NULL,NULL),
 (18,'Nombre de conjoints de femmes enceintes s&eacute;ropositives au VIH d&eacute;pist&eacute;s n&eacute;gatifs',NULL,'SELECT COUNT(DISTINCT pregnant_patient_id) nb\r\nFROM (\r\n  SELECT p.pregnant_patient_id\r\n  FROM ptme_mother_followup pmf, ptme_pregnant_patient p\r\n  WHERE\r\n    pmf.pregnant_patient_id = p.pregnant_patient_id AND\r\n    pmf.spousal_screening_date BETWEEN :startDate AND :endDate AND\r\n    pmf.spousal_screening_result = 0 AND\r\n    pmf.location_id = :locationId\r\n\r\n  UNION\r\n\r\n  SELECT c.pregnant_patient_id\r\n  FROM ptme_consultation c, ptme_prenatal p, ptme_hiv_service h\r\n  WHERE\r\n    c.consultation_id = h.hiv_service_id AND\r\n    (h.hiv_status_at_reception = 1 OR h.test_result = 1) AND\r\n    c.consultation_id = p.consultation_id AND\r\n    c.consultation_date BETWEEN :startDate AND :endDate AND\r\n    p.spousal_screening_result = 0 AND\r\n    c.location_id = :locationId\r\n) _','I18','06b00672-ea33-4da7-a871-45eb6e6e03c5',1,'2018-11-12 08:51:51',1,'2019-01-15 09:15:29',0,NULL,NULL,NULL),
 (19,'Femmes enceintes ou allaitantes depist&eacute;es positives nouvellement mises sous traitement ARV &agrave; M1 du d&eacute;but de TARV',NULL,'SELECT\r\n   count(DISTINCT p.patient_id) nb\r\n\r\nFROM\r\n  (\r\n    SELECT\r\n      DateDebutSuiviPTME, MV.pregnant_patient_id, start_date, arv_status_at_registering, mother_followup_id, pregnancy_outcome\r\n    FROM\r\n      (\r\n        SELECT\r\n          MAX(start_date) DateDebutSuiviPTME, pregnant_patient_id\r\n        FROM ptme_mother_followup WHERE start_date < :startDate AND voided = 0 AND location_id = :locationId\r\n        GROUP BY pregnant_patient_id\r\n      ) MV\r\n      ,\r\n      (SELECT start_date, pregnant_patient_id, arv_status_at_registering, mother_followup_id, pregnancy_outcome FROM ptme_mother_followup) S\r\n    WHERE S.pregnant_patient_id = MV.pregnant_patient_id AND S.start_date = MV.DateDebutSuiviPTME\r\n  ) V\r\n  INNER JOIN (SELECT pregnant_patient_id, patient_id, hiv_care_number FROM ptme_pregnant_patient WHERE voided = 0) p\r\n    ON p.pregnant_patient_id = V.pregnant_patient_id\r\n  LEFT JOIN\r\n    (\r\n      SELECT\r\n        mother, pc.child_id, eating_type\r\n      FROM\r\n        ptme_child pc,\r\n        ptme_child_followup pcf,\r\n        ptme_child_followup_visit pcfv,\r\n        (SELECT MAX(visit_date) MaxVisitDate, child_id FROM ptme_child_followup_visit WHERE visit_date <= :endDate AND location_id = :locationId GROUP BY child_id ) mpcfv\r\n      WHERE\r\n        pc.child_id = pcf.child_followup_id AND pc.child_id = pcfv.child_id AND mpcfv.MaxVisitDate = pcfv.visit_date AND mpcfv.child_id = pcfv.child_id\r\n    ) cf ON cf.mother = p.patient_id\r\n  LEFT JOIN (SELECT value_datetime DateDebutARV, person_id\r\n             FROM obs\r\n             WHERE concept_id IN (159599, 164588, 165032) AND ADDDATE(value_datetime, INTERVAL 1 MONTH) BETWEEN :startDate AND :endDate AND voided = 0 GROUP BY person_id) ARV\r\n    ON ARV.person_id = p.patient_id\r\nWHERE\r\n  (pregnancy_outcome IS NULL OR (pregnancy_outcome IS NOT NULL AND eating_type = 1)) AND\r\n  arv_status_at_registering IN (0,2) AND DateDebutARV IS NOT NULL','I19','593b50f8-f3a9-4fbd-9fd3-508966192e0f',1,'2018-12-05 15:12:02',1,'2019-01-15 09:07:36',0,NULL,NULL,NULL),
