ULL OR (pregnancy_outcome IS NOT NULL AND eating_type = 1)) AND\r\n  arv_status_at_registering IN (0,2) AND DateDebutARV IS NOT NULL','I20','85438351-d26e-4aac-8702-51ddd76d55d2',1,'2018-12-05 15:13:02',1,'2019-01-15 09:07:52',0,NULL,NULL,NULL),
